package main

import "strings"

func FindMarkerPos(packet string, lenDif int) (string, int) {
  differentChars, firstMarkerPos := "", 0
  for _, aRune := range packet {
    if len(differentChars) == lenDif {
      break
    }
    runePosition := strings.IndexRune(differentChars, aRune)
    if runePosition >= 0 {
      differentChars = differentChars[(runePosition+1):]
    }
    differentChars += string(aRune)
    firstMarkerPos += 1
  }
  return differentChars, firstMarkerPos
}

func main() {
  println(FindMarkerPos("bvwbjplbgvbhsrlpgdmjqwftvncz", 14))
  println(FindMarkerPos("nppdvjthqldpwncqszvftbrmjlhg", 14))
  println(FindMarkerPos("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", 14))
  println(FindMarkerPos("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", 14))
  println(FindMarkerPos("", 14))
}

