package main

import "strings"

func FindFirstMarkerPos(packet string) (string, int) {
  fourDifferentChars, firstMarkerPos := "", 0
  for _, aRune := range packet {
    if len(fourDifferentChars) == 4 {
      break
    }
    runePosition := strings.IndexRune(fourDifferentChars, aRune)
    if runePosition >= 0 {
      fourDifferentChars = fourDifferentChars[(runePosition+1):]
    }
    fourDifferentChars += string(aRune)
    firstMarkerPos += 1
  }
  return fourDifferentChars, firstMarkerPos
}

func main() {
  println(FindFirstMarkerPos("bvwbjplbgvbhsrlpgdmjqwftvncz"))
  println(FindFirstMarkerPos("nppdvjthqldpwncqszvftbrmjlhg"))
  println(FindFirstMarkerPos("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg"))
  println(FindFirstMarkerPos("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"))
  println(FindFirstMarkerPos(""))
}

